# height = 17
# width = 106
width = 3
height = 3
def tupper(k):

  def f(x,y):
    d = ((-height * x) - (y % height))
    f = (y/height) >> (-d)
    g = f % 2
    return 0.5 < g

  result = ""
  for y in range(k-1+height, k-1, -1):
    line = ""
    for x in range(0, width):
      if f(x,y):
        line += "*"
      else:
        line += " "
    # print line
    result += line + '\n'
  return result

# k = 
k = 187 * 3
print(tupper(k))
